fun isPalmUpOpen(handLandmarks: List<Point3D>): Boolean {
 val wrist = handLandmarks[0]
 val indexBase = handLandmarks[5]
 val pinkyBase = handLandmarks[17]

 // 1. Finger extension check
 val fingersExtended = listOf(8 to 5, 12 to 9, 16 to 13, 20 to 17).all { (tip, base) ->
 distance(handLandmarks[tip], handLandmarks[base]) > 0.05f
 }

 if (!fingersExtended) return false

 // 2. Palm normal
 val v1 = indexBase - wrist
 val v2 = pinkyBase - wrist
 val normal = crossProduct(v1, v2)

 // 3. Check if palm is facing camera (z < 0)
 return normal.z < -0.1f
}

fun palmCenter(handLandmarks: List<Point3D>): Point3D {
 val indices = listOf(0, 5, 9, 13, 17)
 return indices.map { handLandmarks[it] }
 .reduce { acc, p -> acc + p } / indices.size.toFloat()
}
