+ fun isHorizontalOpenHandPosition(
+ landmarks: List<NormalizedLandmark>,
+ imageWidth: Int,
+ imageHeight: Int
+ ): Boolean {
+ // Check if hand is in horizontal open hand position
+ val wrist = landmarks[0]
+ val indexTip = landmarks[8]
+ val middleTip = landmarks[12]
+ val pinkyTip = landmarks[20]
+
+ val wristX = wrist.x() * imageWidth
+ val indexTipX = indexTip.x() * imageWidth
+ val middleTipX = middleTip.x() * imageWidth
+ val pinkyTipX = pinkyTip.x() * imageWidth
+
+ val wristY = wrist.y() * imageHeight
+ val indexTipY = indexTip.y() * imageHeight
+ val middleTipY = middleTip.y() * imageHeight
+ val pinkyTipY = pinkyTip.y() * imageHeight
+
+ // Hand is in horizontal open hand position if all fingertips are roughly at the same y-coordinate
+ val minY = minOf(indexTipY, middleTipY, pinkyTipY)
+ val maxY = maxOf(indexTipY, middleTipY, pinkyTipY)
+ val isHorizontalOpen = (maxY - minY) < (imageHeight * 0.1)
+
+ Log.d(
+ "HandTracking",
+ "Horizontal open hand position - indexTip: (${indexTipX.toInt()}, ${indexTipY.toInt()}), " +
+ "middleTip: (${middleTipX.toInt()}, ${middleTipY.toInt()}), " +
+ "pinkyTip: (${pinkyTipX.toInt()}, ${pinkyTipY.toInt()}), " +
+ "isHorizontalOpen: $isHorizontalOpen"
+ )
+
+ return isHorizontalOpen
+ }
