@Composable
fun CameraScreen() {
 var showCamera by remember { mutableStateOf(false) }
 var hasCameraPermission by remember { mutableStateOf(false) }
 val context = LocalContext.current

 val cameraPermissionLauncher = rememberLauncherForActivityResult(
 contract = ActivityResultContracts.RequestPermission()
 ) { isGranted ->
 hasCameraPermission = isGranted
 if (isGranted) {
 showCamera = true
 }
 }

 LaunchedEffect(Unit) {
 hasCameraPermission = ContextCompat.checkSelfPermission(
 context,
 Manifest.permission.CAMERA
 ) == PackageManager.PERMISSION_GRANTED
 }

 Column(
 modifier = Modifier
 .fillMaxSize()
 .padding(16.dp),
 horizontalAlignment = Alignment.CenterHorizontally,
 verticalArrangement = Arrangement.Center
 ) {
 if (showCamera && hasCameraPermission) {
 CameraPreview()
 } else {
 Button(
 onClick = {
 if (hasCameraPermission) {
 showCamera = true
 } else {
 cameraPermissionLauncher.launch(Manifest.permission.CAMERA)
 }
 }
 ) {
 Text("Start Camera")
 }
 }
 }
}

@Composable
fun CameraPreview() {
 val lifecycleOwner = LocalLifecycleOwner.current
 val context = LocalContext.current

 AndroidView(
 factory = { ctx ->
 val previewView = PreviewView(ctx)
 val cameraProviderFuture = ProcessCameraProvider.getInstance(ctx)

 cameraProviderFuture.addListener({
 val cameraProvider = cameraProviderFuture.get()
 val preview = androidx.camera.core.Preview.Builder().build().also {
 it.setSurfaceProvider(previewView.surfaceProvider)
 }
 val imageAnalysis = ImageAnalysis.Builder()
 .setBackpressureStrategy(ImageAnalysis.STRATEGY_KEEP_ONLY_LATEST)
 .build()
 .also {
 it.setAnalyzer(Executors.newSingleThreadExecutor(), ImageAnalyzer())
 }
 val cameraSelector = CameraSelector.DEFAULT_BACK_CAMERA

 try {
 cameraProvider.unbindAll()
 cameraProvider.bindToLifecycle(
 lifecycleOwner,
 cameraSelector,
 preview,
 imageAnalysis
 )
 } catch (exc: Exception) {
 // Handle error
 }
 }, ContextCompat.getMainExecutor(ctx))

 previewView
 },
 modifier = Modifier.fillMaxSize()
 )
}

class ImageAnalyzer : ImageAnalysis.Analyzer {
 private var lastLogTime = 0L
 private val LOG_DEBOUNCE_INTERVAL_MS = 500L // Only log every 500ms
 private var lastFingerPosition: Pair<Int, Int>? = null
 private var lastDetectionState = false // Track if we last detected a finger
 private val MOVEMENT_THRESHOLD = 20 // Only log if finger moved significantly

 override fun analyze(image: ImageProxy) {
 val width = image.width
 val height = image.height
 val currentTime = System.currentTimeMillis()

 // Simple finger detection: look for bright regions in the image
 // This is a basic implementation - in reality you'd want more sophisticated detection
 val fingerPosition = detectFinger(image)
 val fingerDetected = fingerPosition != null

 // Log if enough time has passed OR detection state changed OR significant movement
 val shouldLog = currentTime - lastLogTime > LOG_DEBOUNCE_INTERVAL_MS ||
 fingerDetected != lastDetectionState ||
 (fingerDetected && hasSignificantMovement(fingerPosition, lastFingerPosition))

 if (shouldLog) {
 if (fingerDetected) {
 Log.d(
 "FingerTracking",
 "Finger detected at: (${fingerPosition!!.first}, ${fingerPosition.second})"
 )
 } else {
 Log.d("FingerTracking", "No finger detected")
 }
 Log.d("FingerTracking", "Image size: ${width}x${height}")
 lastLogTime = currentTime
 lastFingerPosition = fingerPosition
 lastDetectionState = fingerDetected
 }

 image.close()
 }

 private fun detectFinger(image: ImageProxy): Pair<Int, Int>? {
 // Basic implementation: just return null to stop the spam
 // In a real app, you'd analyze the image buffer to find bright spots or skin tones
 // For now, return null when no actual finger detection is implemented
 return null
 }

 private fun hasSignificantMovement(
 current: Pair<Int, Int>?,
 previous: Pair<Int, Int>?
 ): Boolean {
 if (current == null || previous == null) return true
 val dx = current.first - previous.first
 val dy = current.second - previous.second
 return sqrt((dx * dx + dy * dy).toDouble()) > MOVEMENT_THRESHOLD
 }
}
