 }
+
+ private fun detectFinger(image: ImageProxy): Pair<Int, Int>? {
+ // Basic implementation: just return null to stop the spam
+ // In a real app, you'd analyze the image buffer to find bright spots or skin tones
+ // For now, return null when no actual finger detection is implemented
+ return null
+ }
+
+ private fun hasSignificantMovement(
+ current: Pair<Int, Int>?,
+ previous: Pair<Int, Int>?
+ ): Boolean {
+ if (current == null || previous == null) return true
+ val dx = current.first - previous.first
+ val dy = current.second - previous.second
+ return sqrt((dx * dx + dy * dy).toDouble()) > MOVEMENT_THRESHOLD
+ }
 }
