class MainActivity : ComponentActivity(), HandLandmarkerHelper.LandmarkerListener {

 internal lateinit var backgroundExecutor: ExecutorService
 internal lateinit var handLandmarkerHelper: HandLandmarkerHelper
 internal var isHandLandmarkerInitialized = false

 // Shared states for fingertip position and input image size
 private val _fingerTipPosition = mutableStateOf<Offset?>(null)
 val fingerTipPosition: State<Offset?> get() = _fingerTipPosition

 // Drawing path state
 private val _drawingPoints = mutableStateOf<List<Offset>>(emptyList())
 val drawingPoints: State<List<Offset>> get() = _drawingPoints

 // Hand gesture state
 private val _isHandOpen = mutableStateOf(false)
 val isHandOpen: State<Boolean> get() = _isHandOpen

 private val _isLeftHand = mutableStateOf(false)
 val isLeftHand: State<Boolean> get() = _isLeftHand

 private val _previewSize = mutableStateOf<Pair<Int, Int>?>(null)
 val previewSize: State<Pair<Int, Int>?> get() = _previewSize

 override fun onCreate(savedInstanceState: Bundle?) {
 super.onCreate(savedInstanceState)

 // Initialize background executor
 backgroundExecutor = Executors.newSingleThreadExecutor()

 enableEdgeToEdge()
 setContent {
 FingerdrawingTheme {
 Surface(
 modifier = Modifier.fillMaxSize(),
 color = MaterialTheme.colorScheme.background
 ) {
 // Pass fingerTipPosition and previewSize as state into the camera screen
 CameraScreen(
 fingerTipPosition = fingerTipPosition,
 drawingPoints = drawingPoints,
 isHandOpen = isHandOpen,
 isLeftHand = isLeftHand,
 previewSize = previewSize
 )
 }
 }
 }
 }

 override fun onDestroy() {
 super.onDestroy()
 backgroundExecutor.shutdown()
 if (isHandLandmarkerInitialized) {
 handLandmarkerHelper.clearHandLandmarker()
 }
 }

 override fun onError(error: String, errorCode: Int) {
 Log.e("HandTracking", "Hand landmarker error: $error")
 }

 override fun onResults(resultBundle: HandLandmarkerHelper.ResultBundle) {
 val results = resultBundle.results
 if (results.isNotEmpty()) {
 val handResult = results.first()
 if (handResult.landmarks().isNotEmpty()) {
 val landmarks = handResult.landmarks().first()
 // Get index finger tip (landmark 8) and wrist (landmark 0)
 if (landmarks.size > 8) {
 val fingerTip = landmarks[8]
 val wrist = landmarks[0]

 // Convert normalized coordinates to pixel coordinates
 val fingerTipX = (fingerTip.x() * resultBundle.inputImageWidth)
 val fingerTipY = (fingerTip.y() * resultBundle.inputImageHeight)
 val wristX = (wrist.x() * resultBundle.inputImageWidth)
 val wristY = (wrist.y() * resultBundle.inputImageHeight)

 // Check if hand is left or right
 val isLeft = checkIfLeftHand(
 landmarks,
 resultBundle.inputImageWidth,
 resultBundle.inputImageHeight
 )
 _isLeftHand.value = isLeft

 // Check if hand is open (5 fingers extended) or closed (fist)
 val isOpen = checkIfHandIsOpen(
 landmarks,
 resultBundle.inputImageWidth,
 resultBundle.inputImageHeight
 )
 _isHandOpen.value = isOpen

 Log.d(
 "HandTracking",
 "Hand detected - Finger tip: (${fingerTipX.toInt()}, ${fingerTipY.toInt()}), " +
 "Wrist: (${wristX.toInt()}, ${wristY.toInt()}), " +
 "Left hand: $isLeft, " +
 "Hand open: $isOpen, " +
 "Inference time: ${resultBundle.inferenceTime}ms"
 )

 // Update shared state for the fingertip and preview size
 _fingerTipPosition.value = Offset(fingerTipX, fingerTipY)
 _previewSize.value =
 Pair(resultBundle.inputImageWidth, resultBundle.inputImageHeight)

 // Only add drawing points if hand is open
 if (isOpen && isLeft) {
 _drawingPoints.value = _drawingPoints.value + Offset(fingerTipX, fingerTipY)
 }
 }
 } else {
 _fingerTipPosition.value = null
 }
 } else {
 _fingerTipPosition.value = null
 }
 }

 fun checkIfLeftHand(
 landmarks: List<NormalizedLandmark>,
 imageWidth: Int,
 imageHeight: Int
 ): Boolean {
 // Simple left hand detection logic: check if wrist is on the left side of the image
 val wrist = landmarks[0]
 val wristX = wrist.x() * imageWidth
 val isLeft = wristX < (imageWidth / 2)
 Log.d(
 "HandTracking",
 "Left hand check - wristX: ${wristX.toInt()}, imageWidth/2: ${imageWidth / 2}, isLeft: $isLeft"
 )
 return isLeft
 }

 fun checkIfHandIsOpen(
 landmarks: List<NormalizedLandmark>,
 imageWidth: Int,
 imageHeight: Int
 ): Boolean {
 // Simple hand open detection logic: check if fingers are above wrist
 val wrist = landmarks[0]
 val wristX = wrist.x() * imageWidth
 val wristY = wrist.y() * imageHeight

 val fingerTipIndices = listOf(4, 8, 12, 16, 20) // tip of thumb, index, middle, ring, pinky
 var fingersAboveWrist = 0

 for (index in fingerTipIndices) {
 if (index < landmarks.size) {
 val fingerTip = landmarks[index]
 val fingerTipX = fingerTip.x() * imageWidth
 val fingerTipY = fingerTip.y() * imageHeight

 if (fingerTipY < wristY) { // finger is above wrist (smaller y = higher up)
 fingersAboveWrist++
 }
// Log.d(
// "HandTracking",
// "Finger $index - tip: (${fingerTipX.toInt()}, ${fingerTipY.toInt()}), above wrist: ${fingerTipY < wristY}"
// )
 }
 }

 val isOpen = fingersAboveWrist < 4 // at least 4 fingers above wrist = open hand
// Log.d(
// "HandTracking",
// "Hand gesture - wrist: (${wristX.toInt()}, ${wristY.toInt()}), fingers above wrist: $fingersAboveWrist/5, isOpen: $isOpen"
// )
 return isOpen
 }
}

